mapper.select:
select auth_no
from has_authority
where id=#{id}

select id, pwd, name, nickName, tel, email, birth, sex, location1, location2, prt_Pattern as prtPattern, info_Status as infoStatus 
from member 
where id=#{id}

select id, pwd, name, nickName, tel, email, birth, sex, location1, location2, prt_Pattern as prtPattern, info_Status as infoStatus 
from member 
where nickName=#{nickName}

select count(*) 
from member 
where email=#{email}

select id
from member
where name=#{name} AND email=#{email}

select id, pwd, name, nickName, tel, email, birth, sex, location1, location2, prt_Pattern as prtPattern, info_Status as infoStatus
from member
where id=#{id} AND name=#{name} AND email=#{email}

select count(fail_cnt)
from login
where id=#{id}

select fail_cnt
from login
where id=#{id}

mapper.update:
update login
set fail_cnt=fail_cnt+1
where id=#{id}

update member
set pwd=#{pwd}, nickName=#{nickName}, tel=#{tel}, location=#{address}, prt_Pattern=#{prtPattern}, info_Status=#{infoStatus}
where id=#{id}

update member
set pwd=#{pwd}
where id=#{id}
